close all;clear all;datos850=dlmread('EDC850.dat','\t',0,0);	% '\t'="TAB"; como la primer fila es el encabezado, se omite esta fila en la lecturadatos940=dlmread('EDC940.dat','\t',0,0);	% '\t'="TAB"; como la primer fila es el encabezado, se omite esta fila en la lecturasim=dlmread('transferencias_datos.txt','\t',1,0);	% '\t'="TAB"; como la primer fila es el encabezado, se omite esta fila en la lecturafigure()semilogy(datos850(:,1),datos850(:,2)*1e-3,"b", "linewidth", 2)hold on;semilogy(sim(:,1),sim(:,3),"r", "linewidth", 2)grid on;ylabel("Forward Current (mA)");xlabel("Forward Voltage (V)");legend("Datasheet","Fit");title("Transferencia 850nm")xlim([2 4.5])ylim([1e-3 10])figure()semilogy(datos940(:,1),datos940(:,2)*1e-3,"b", "linewidth", 2)hold on;semilogy(sim(:,1),sim(:,4),"r", "linewidth", 2)grid on;ylabel("Forward Current (mA)");xlabel("Forward Voltage (V)");legend("Datasheet","Fit");title("Transferencia 940nm")xlim([2 4.5])ylim([1e-3 10])